mapper.select:
select

from plugins WHERE 1=1

  and id = #{id,jdbcType=INTEGER}


  and header = #{header,jdbcType=VARCHAR}


  and platform = #{platform,jdbcType=VARCHAR}


  and type = #{type,jdbcType=VARCHAR}


  and version = #{version,jdbcType=VARCHAR}


  and pluginname = #{pluginname,jdbcType=VARCHAR}


  and ip = #{ip,jdbcType=VARCHAR}


  and servicename = #{servicename,jdbcType=VARCHAR}


  and namespace = #{namespace,jdbcType=VARCHAR}


  and timestamp = #{timestamp,jdbcType=BIGINT}

order by `timestamp` DESC

mapper.delete:
delete from plugins where 1=1

  and id = #{id,jdbcType=INTEGER}


  and header = #{header,jdbcType=VARCHAR}


  and platform = #{platform,jdbcType=VARCHAR}


  and type = #{type,jdbcType=VARCHAR}


  and version = #{version,jdbcType=VARCHAR}


  and pluginname = #{pluginname,jdbcType=VARCHAR}


  and ip = #{ip,jdbcType=VARCHAR}


  and servicename = #{servicename,jdbcType=VARCHAR}


  and namespace = #{namespace,jdbcType=VARCHAR}


  and timestamp = #{timestamp,jdbcType=BIGINT}
